import React, { useEffect, useState } from 'react'
import '../../App.css'
import '../../Styles/News.css'
import NewsBig from './NewsBig' 

export default function NewsListBig(props) {

    const bigNews = props.bigNews 

    const [currentPage, setCurrentPage] = useState(1);
    const [newsPerPage, setNewsPerPage] = useState(4); 


    useEffect(()=>{ 
        if (window.innerWidth <= 1150) { 
            setNewsPerPage(3)
        } else {
            setNewsPerPage(4)
        }
        if (window.innerWidth <= 850) { 

        }
        function handleResize() {
            if (window.innerWidth <= 1150) { 
                setNewsPerPage(3)
            } else {
                setNewsPerPage(4)
            }
        }
        window.addEventListener("resize", handleResize);
        return (()=>{
            window.removeEventListener("resize", handleResize);
        })
    }, [])

    const choosePage = (event) => {
        if (Number(event.target.id) === 0) {
            setCurrentPage(currentPage)
        } else if (Number(event.target.id) === -1) {
            if (currentPage > 1) {
                setCurrentPage(currentPage - 1)
            } else {
                setCurrentPage(1);
            }
        } else if (Number(event.target.id) === 999) {
            setCurrentPage(currentPage + 1)
        } else {
            setCurrentPage(Number(event.target.id))
        }
    }

    const indexOfLastNews = currentPage * newsPerPage;
    const indexOfFirstNews = indexOfLastNews - newsPerPage;
    const currentNews = oldestNews.slice(indexOfFirstNews, indexOfLastNews);
    const pageNumbers = [];
    for (let i = 1; i <= Math.ceil(oldestNews.length / newsPerPage); i++) {
        pageNumbers.push(i);
    }

    const pages = [];

    if (currentPage === 2) {
        pages.push(currentPage - 1, currentPage, currentPage + 1);
    } else {
        if (currentPage === 1) {
            pages.push(currentPage, currentPage + 1, currentPage + 2 );
        } else if (currentPage === 2) {
            pages.push(currentPage - 1, currentPage, currentPage + 1);
        } else if (currentPage > 2 && currentPage < pageNumbers.length - 1) {
            pages.push(currentPage -1, currentPage, currentPage + 1);
        } else if (currentPage === pageNumbers.length - 1) {
            pages.push(currentPage - 1, currentPage, currentPage + 1);
        } else {
            pages.push(currentPage - 2, currentPage - 1, currentPage);
        }
    }

    return (
        <div className="newslist-big flex">
        {
            bigNews.map((item, index) => {
                return (
                    <NewsBig
                        key={index}
                        bigNews={item}
                    />
                )
            })
        }
    </div>
    )
}
