import React, { useEffect, useState } from 'react'
import '../../Styles/Product.css' 
import '../../App.css'  
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCheckCircle, faShip, faUndo } from '@fortawesome/free-solid-svg-icons'
import ReactStars from "react-rating-stars-component";

export default function ProductDetail() {  

    const [imgIndex, setImgIndex] = useState(0)
    const [hover, setHover] = useState(false)

    useEffect(()=>{   
        if (!hover) {
            var slide = setInterval(() => {
                setImgIndex(imgIndex => imgIndex + 1);
            }, 2000);  
        }
        return (()=>{
            clearInterval(slide)
        })
    },[hover]) 


    const product = {
        productName: 'GPS Tracker cho chó',
        productRating: [
            {
                ratingName: 'Brian Fry',
                ratingTitle: 'Very pleased with the tracker',
                ratingContent: 'Very pleased with the tracker, it is easy to use anworth the money.',
                ratingStart: 4
            }
        ],
        productColor: [
            {
                name: 'White',
                productImg: [
                    "https://tractive.com/static/images/product-images/trnjawh/tractive-gps-dog-4-packaging-en-400w.jpg",
                    "https://tractive.com/static/images/product-images/trnjawh/tractive-gps-dog-4-tracker-400w.jpg",
                    "https://tractive.com/static/images/product-images/trnjawh/tractive-gps-dog-4-tracker-front-400w.jpg",
                    "https://tractive.com/static/images/product-images/trnjawh/tractive-gps-dog-4-tracker-top.jpg",
                    "https://tractive.com/static/images/product-images/trnjawh/tractive-gps-dog-4-tracker-back-400w.jpg",
                    "https://tractive.com/static/images/product-images/trnjawh/tractive-gps-dog-4-tracker-top.jpg",
                    "https://tractive.com/static/images/product-images/trnjawh/tractive-gps-dog-4-packaging-en-400w.jpg"
                ]
            },
            {
                name: 'Pink',
                productImg: [
                    "https://tractive.com/static/images/product-images/trnjawh/tractive-gps-dog-4-packaging-en-400w.jpg",
                    "https://tractive.com/static/images/product-images/trnjawh/tractive-gps-dog-4-tracker-400w.jpg",
                    "https://tractive.com/static/images/product-images/trnjawh/tractive-gps-dog-4-tracker-front-400w.jpg",
                    "https://tractive.com/static/images/product-images/trnjawh/tractive-gps-dog-4-tracker-top.jpg",
                    "https://tractive.com/static/images/product-images/trnjawh/tractive-gps-dog-4-tracker-back-400w.jpg",
                    "https://tractive.com/static/images/product-images/trnjawh/tractive-gps-dog-4-tracker-top.jpg",
                    "https://tractive.com/static/images/product-images/trnjawh/tractive-gps-dog-4-packaging-en-400w.jpg"
                ]
            }
        ],
        productFeature: [
            "Theo dõi vị trí và giám sát hoạt động trên toàn thế giới",
            "Chế độ TRỰC TIẾP - cập nhật vị trí cứ sau 2-3 giây",
            "Nhận thông báo nếu con chó của bạn đi lang thang quá xa nhà",
            "Tận hưởng trải nghiệm theo dõi tốt nhất với công nghệ mạng mới nhất",
            "100% không thấm nước và chống va đập",
            "Pin lâu dài lên đến 5 ngày",
        ]
    }

    if (imgIndex === product.productColor[0].productImg.length) {
        setImgIndex(0);
    }  

    let width = 0;

    if (imgIndex === 1 || imgIndex === 2) {
        width = 0
    } 
    if (imgIndex >= 3) {
        width = 54
    }  
    if (imgIndex >= 4 && imgIndex) {
        width = (imgIndex - 2) * 54
    } 
    if (imgIndex === product.productColor[0].productImg.length - 2) {
        width = (imgIndex - 3) * 54 
    }
    if (imgIndex === product.productColor[0].productImg.length - 1) {
        width = (imgIndex - 4) * 54 
    } 

    const ratingStar = {
        size: 14,
        value: 5,
        edit: false,
        activeColor: "#fc2754",
        color: "#ddd",
        isHalf: true
    }

    return (
        <div className="ProductDetail flex">   
            <div className="productdetail-image-box">
                <div 
                    className="productdetail-image flex"
                    onMouseEnter={()=>{
                        setHover(true)   
                    }}
                    onMouseLeave={()=>{
                        setHover(false) 
                    }} 
                >
                    {
                        product.productColor[0].productImg.map((item, index) => {
                            return (
                                <div  
                                    key={index}
                                    className="productdetail-image-item"
                                    style={{
                                        transform: `translateX(-${320 * imgIndex}px)`
                                    }}
                                >
                                    <img src={item} alt=""/>
                                </div>
                            )
                        })
                    } 
                </div>
                <div className="productdetail-image-small flex">
                    <div 
                        onMouseEnter={()=>{
                            setHover(true)   
                        }}
                        onMouseLeave={()=>{
                            setHover(false) 
                        }} 
                        className="productdetail-image-small-btn"
                        onClick={()=>{
                            if (imgIndex > 0) {
                                setImgIndex(imgIndex => imgIndex - 1);
                            }
                        }}>
                        <img src="https://tractive.com/static/images/arrow-slide-left.svg" alt=""></img>
                    </div>
                    <div className="productdetail-image-small-list flex">
                        {
                            product.productColor[0].productImg.map((item, index) => {
                                return (
                                    <img 
                                        src={item} 
                                        alt=""
                                        key={index}
                                        onClick={()=>{
                                            setImgIndex(index)
                                        }} 
                                        style={{
                                            transform: `translateX(-${width}px)`
                                        }}
                                        className={imgIndex === index ? 'outline' : ''}
                                    />
                                )
                            })
                        }
                    </div>
                    <div 
                        className="productdetail-image-small-btn"
                        onClick={()=>{
                            setImgIndex(imgIndex => imgIndex + 1);
                        }}>
                        <img src="https://tractive.com/static/images/arrow-slide-right.svg" alt=""></img>
                    </div>
                </div>
            </div>  
            <div className="productdetail-info"> 
                <h1>{product.productName}</h1>
                <div className="productdetail-start flex">
                    <ReactStars {...ratingStar} />
                    <p>{product.productRating.length} đánh giá</p>
                </div>
                <ul>
                    {
                        product.productFeature.map((item, index) => {
                            return (
                            <li key={index}>{item}</li>
                            )
                        })
                    }
                </ul>
                <p>Gặp gỡ thiết bị theo dõi GPS mới nhất của chúng tôi dành cho chó - thiết bị theo dõi chó với độ chính xác và tuổi thọ pin tốt hơn. Nó kết hợp công nghệ mạng mới nhất với các tính năng phổ biến nhất của chúng tôi để bạn yên tâm hơn nữa. Và với phạm vi không giới hạn, nó cho phép bạn xem vị trí và mức độ hoạt động của con chó của bạn ở hầu hết mọi nơi trên thế giới.</p>
            </div>
            <div className="productdetail-addtocart flex-col">
                <ul className="productdetail-flag-info">
                    <ul className="productdetail-flag-info-list">
                        <li><FontAwesomeIcon icon={faCheckCircle} className="icon"/>Chấp nhận mọi thẻ thanh toán</li>
                        <li><FontAwesomeIcon icon={faShip} className="icon"/>Ships toàn quốc</li>
                        <li><FontAwesomeIcon icon={faUndo} className="icon"/>Đổi trả trong vòng 14 ngày</li>
                    </ul>
                    <div class="triangle"></div>
                </ul>
                <p className="productdetail-price">1 000 000đ</p>
                <div className="productdetail-addtocart-btn">
                    Thêm vào giỏ hàng
                </div>
            </div> 
        </div>
    )
}
