import React, { useState } from 'react'
import '../../Styles/Product.css'
import '../../App.css'  

export default function ProductDetail() {  

    const [imgIndex, setImgIndex] = useState(0)
    const [hover, setHover] = useState(false)

    // useState(()=>{  
    //     console.log(hover) 
    //     // var slide = setInterval(() => {
    //     //     setImgIndex(imgIndex => imgIndex + 1);
    //     // }, 1000);  
    //     // return (()=>{
    //     //     clearInterval(slide)
    //     // })
    // },[hover])
    useEffect(() => {
        console.log(hover)
        // if (hover === false) {
        //     var interval = setInterval(() => {
        //         setImgIndex(imgIndex => imgIndex + 1);
        //     }, 1000);
        // }
        // return() => {
        //     clearInterval(interval);
        // }
    },[hover])

    const imgLinks = [
        "https://tractive.com/static/images/product-images/trnjawh/tractive-gps-dog-4-packaging-en-400w.jpg",
        "https://tractive.com/static/images/product-images/trnjawh/tractive-gps-dog-4-tracker-400w.jpg",
        "https://tractive.com/static/images/product-images/trnjawh/tractive-gps-dog-4-tracker-front-400w.jpg",
        "https://tractive.com/static/images/product-images/trnjawh/tractive-gps-dog-4-tracker-top.jpg",
        "https://tractive.com/static/images/product-images/trnjawh/tractive-gps-dog-4-tracker-back-400w.jpg",
        "https://tractive.com/static/images/product-images/trnjawh/tractive-gps-dog-4-tracker-top.jpg",
    ]

    if (imgIndex === imgLinks.length) {
        setImgIndex(0);
    } 

    const smallWidth = 0;

    return (
        <div className="ProductDetail flex">   
            <div className="productdetail-image-box">
                <div 
                    className="productdetail-image flex"
                    onMouseEnter={()=>{
                        setHover(true)  
                        console.log("c")
                    }}
                    onMouseLeave={()=>{
                        setHover(false) 
                    }} 
                >
                    {
                        imgLinks.map((item, index) => {
                            return (
                                <div  
                                    key={index}
                                    className="productdetail-image-item"
                                    style={{
                                        transform: `translateX(-${320 * imgIndex}px)`
                                    }}
                                >
                                    <img src={item} alt=""/>
                                </div>
                            )
                        })
                    } 
                </div>
                <div className="productdetail-image-small flex">
                    <div className="productdetail-image-small-btn">
                        <img src="https://tractive.com/static/images/arrow-slide-left.svg" alt=""></img>
                    </div>
                    <div className="productdetail-image-small-list flex">
                        {
                            imgLinks.map((item, index) => {
                                return (
                                    <img 
                                        src={item} 
                                        alt=""
                                        key={index}
                                        style={{
                                            transform: `translateX(-${smallWidth}px)`
                                        }}
                                        className={imgIndex === index ? 'outline' : ''}
                                    />
                                )
                            })
                        }
                    </div>
                    <div className="productdetail-image-small-btn">
                        <img src="https://tractive.com/static/images/arrow-slide-right.svg" alt=""></img>
                    </div>
                </div>
            </div>  
            <div className="productdetail-info">

            </div>
            <div className="productdetail-addtocart">

            </div>
        </div>
    )
}
