import React, { useState } from 'react'
import '../../App.css'
import '../../Styles/News.css'
import NewsBig from './NewsBig'
import News from './News'

export default function NewsList(props) {

    const news = props.news
    const bigNews = props.bigNews.filter
    const oldestNews = props.oldestNews 
    const [cateTab, setCateTab] = useState(0)

    const cate = Object.values(news.reduce((a, {newCate}) => {
        a[newCate] = a[newCate] || {newCate, count: 0};
        a[newCate].count++;
        return a;
    }, Object.create(null)));

    if (bigNews) console.log(bigNews.filter((item) => {return item.newCate === "zxc"}))

    return (
        <div className="newslist">
            <div className="newslist-tab flex">
                <div 
                    onClick={()=>{
                        setCateTab(0)
                    }} 
                    className={cateTab === 0 ? "newslist-box flex newslist-box-active" : "newslist-box flex"}>
                    All
                    <div className="news-count flex-center">{news.length}</div>
                </div>
                { cate &&
                    cate.map((item, index) => {
                        return (
                            <div 
                                onClick={()=>{
                                    setCateTab(index + 1)
                                }}
                                key={index}
                                className={cateTab === index + 1? "newslist-box flex newslist-box-active" : "newslist-box flex"}>
                                {item.newCate} 
                                <div className="news-count flex-center">{item.count}</div>
                            </div>
                        )
                    })
                }
            </div>
            <div className="newslist-big flex">
                {
                    bigNews.map((item, index) => {
                        return (
                            <NewsBig
                                key={index}
                                bigNews={item}
                            />
                        )
                    })
                }
            </div>
            <div className="newslist-small flex"> 
                {
                    oldestNews.map((item, index) => {
                        return (
                            <News
                                key={index}
                                news={item}
                            />
                        )
                    })
                }
            </div>
            <div className="newslist-pag flex">

            </div>
        </div>
    )
}
