import React, { useState } from 'react'
import '../../App.css'
import '../../Styles/Login.css'

export default function Login(props) {

    const [tab, setTab] = useState(0)
    const [email, setEmail] = useState("")
    const [name, setName] = useState("")
    const [password, setPassword] = useState("")

    const sendAccount = (event) => {
        event.preventDefault()
        if (tab === 0) {
            console.log(".login")
        } else {
            axios.post('http://localhost:4000/users/register', {
                userName: user.registerName,
                userEmail: user.registerEmail,
                userPassword: user.registerPassword,
                userRole: "user"
            })
            .then(res => {
                setArrSuccess(arrSuccess=>[...arrSuccess, res.data])
                setTimeout(()=> {
                    window.location.reload(false);
                    document.body.style.overflow = 'unset';
                }, 1000)
            })
            .catch(err => {
                setArrErr(arrErr=>[...arrErr, err.response.data]);
            })
        }
    }

    return (
        <div className="login flex-col">
            <div className="login-title">
                <strong>Thiết bị định vị</strong> cho chó và mèo
            </div>
            <div className="login-banner"> 
            </div>
            { tab === 0 &&
                <div className="login-box flex-col">
                    <div className="login-box-title"><strong>Sign</strong> in</div>
                    <form className="flex-col" onSubmit={sendAccount}>
                        <label>Email</label>
                        <input
                            value={email}
                            onChange={(event)=>{
                                setEmail(event.target.value)
                            }}
                        ></input>
                        <label>Password</label>
                        <input
                            value={password}
                            onChange={(event)=>{
                                setPassword(event.target.value)
                            }}
                        ></input>
                        <button>Sign in</button>
                    </form>
                    <div className="login-forgot">Forgot password?</div>
                    <div className="login-register" onClick={()=>{setTab(1)}}> New user? Create account</div>
                </div>
            }
            { tab === 1 &&
                <div className="login-box flex-col">
                    <div className="login-box-title"><strong>Create</strong> acount</div>
                    <form className="flex-col">
                        <label>Name *</label>
                        <input
                            value={name}
                            onChange={(event)=>{
                                setName(event.target.value)
                            }}
                        ></input>
                        <label>Email *</label>
                        <input
                            value={email}
                            onChange={(event)=>{
                                setEmail(event.target.value)
                            }}
                        ></input>
                        <label>Password *</label>
                        <input
                            value={password}
                            onChange={(event)=>{
                                setPassword(event.target.value)
                            }}
                        ></input>
                        <button>Create account</button>
                    </form> 
                    <div className="login-register" onClick={()=>{setTab(0)}}>Already have an account?</div>
                </div>
            }
        </div>
    )
}
