import React from 'react'
import '../../App.css'
import '../../Styles/News.css'

export default function News(props) {

    const oldestNews = props.oldestNews 

    let date = ""
    if (oldestNews) {
        oldestNews.newTime
    }
    return (
        <div className="news">
            {oldestNews &&
                <div className="news-img">
                    <img src={oldestNews.newImg} alt=""></img>
                    <div className="news-cate">{oldestNews.newCate}</div>
                </div>
            }
            {oldestNews &&
                <div className="news-time">{date}</div>
            }
            {oldestNews &&
                <div className="news-title">{oldestNews.newTitle}</div>
            }
            {oldestNews &&
                <div className="news-content">{oldestNews.newContent.replace(/<[^>]+>/g, '')}</div>
            }
            {oldestNews &&
                <div className="news-readmore">read more...</div>
            }
        </div>
    )
}
