import React, { useState } from 'react'
import '../../Styles/Product.css'
import '../../App.css'  

export default function ProductDetail() {  

    const [imgIndex, setImgIndex] = useState(1)

    useState(()=>{
        const slide = setInterval(() => {
            setImgIndex(imgIndex + 1)
        }, 1000);
        return (()=>{
            clearInterval(slide)
        })
    },[])

    return (
        <div className="ProductDetail flex">   
            <div className="productdetail-image flex">
                <div className="productdetail-image-item">
                    <img src="https://tractive.com/static/images/product-images/trnjawh/tractive-gps-dog-4-packaging-en-400w.jpg" />
                </div>
                <div className="productdetail-image-item">
                    <img src="https://tractive.com/static/images/product-images/trnjawh/tractive-gps-dog-4-tracker-400w.jpg"/>
                </div>
                <div className="productdetail-image-item">
                    <img src="https://tractive.com/static/images/product-images/trnjawh/tractive-gps-dog-4-tracker-front-400w.jpg"/>
                </div>
                <div className="productdetail-image-item">
                    <img src="https://tractive.com/static/images/product-images/trnjawh/tractive-gps-dog-4-tracker-top.jpg"/>
                </div> 
            </div>  
            <div className="productdetail-info">

            </div>
            <div className="productdetail-addtocart">

            </div>
        </div>
    )
}
