import React, { useEffect, useState } from 'react'
import '../../App.css'
import '../../Styles/News.css'

export default function News(props) {

    const oldestNews = props.oldestNews 

    const [date, setDate] = useState("")

    useEffect(()=>{
        const monthNames = [
            "January", "February", "March", "April", "May", "June",
            "July", "August", "September", "October", "November", "December"
        ];
        if (oldestNews) {
            const date = new Date(oldestNews.newTime)
            const day = date.getDate()
            const month = date.getMonth()
            const year = date.getFullYear()
            setDate(`${day} ${monthNames[month]} ${year}`)
        }
    },[])

    return (
        <div className="news">
            {oldestNews &&
                <div className="news-img">
                    <img src={oldestNews.newImg} alt=""></img>
                    <div className="news-cate">{oldestNews.newCate}</div>
                </div>
            }
            {oldestNews &&
                <div className="news-time">{date}</div>
            }
            {oldestNews &&
                <div className="news-title">{oldestNews.newTitle}</div>
            }
            {oldestNews &&
                <div className="news-content">{oldestNews.newContent.replace(/<[^>]+>/g, '')}</div>
            }
            {oldestNews &&
                <div className="news-readmore">read more...</div>
            }
        </div>
    )
}
